import React,{useState} from 'react';
import RFC from '../pages/RFC';

const RFCContainer = () =>{
	const [rfc,setRFC]=useState("");
	const [form,setForm]=useState({nombre:"",paterno:"",fechaNacimiento:""});

	const onChange = (e) =>{
		setForm({...form,[e.target.name]:e.target.value});
	}

	return(
		<RFC
			rfc={rfc}
			form={form}
			onChange={onChange}
		/>
	)
};
