import React from 'react';
const Api = ({consumir, perfiles, onChange, form}) =>(
    <div className="principal">
        <div className="container-form-git">
            <form className="form">
                <div className="form-group">
                    <label>Ingrese el Nombre del Usuario a Buscar</label>
                    <input onChange={onChange} value={form.usuario} className="form-control" type="text" name="usuario" id="usuario"/>
                </div>
                <div className="text-center mt-3">
                    <button onClick={consumir} className="btn btn-outline-primary">Buscar Usuario</button>
                </div>
            </form>
        </div>

        <div className="mt-3">
            {
                perfiles.map(
                    (item, i) => (
                        <div>
                            <h2>holo</h2>
                            <img src={item.avatar_url} alt="Perfil" />
                            <a target="_blank" rel="noreferrer" href={item.html_url} key={i}>{item.login}</a>
                        </div>
                    )
                )
            }
        </div>

    </div>
);

export default Api;
